// src/pages/Cart.jsx
import React, { useState } from "react";
import "../styling/Cart.css"; // CSS file for styling

export default function Cart() {
  const [cartItems, setCartItems] = useState([
    {
      id: 1,
      name: "Wireless Headphones",
      price: 80,
      quantity: 1,
      image: "https://images-na.ssl-images-amazon.com/images/I/71o8Q5XJS5L._AC_SL1500_.jpg",
    },
    {
      id: 2,
      name: "Smart Watch",
      price: 120,
      quantity: 2,
      image: "https://m.media-amazon.com/images/I/71YB2xkAxgL._AC_SL1500_.jpg",
    },
  ]);

  // Increase quantity
  const increaseQty = (id) => {
    setCartItems(
      cartItems.map((item) =>
        item.id === id ? { ...item, quantity: item.quantity + 1 } : item
      )
    );
  };

  // Decrease quantity
  const decreaseQty = (id) => {
    setCartItems(
      cartItems.map((item) =>
        item.id === id && item.quantity > 1
          ? { ...item, quantity: item.quantity - 1 }
          : item
      )
    );
  };

  // Remove item from cart
  const removeItem = (id) => {
    setCartItems(cartItems.filter((item) => item.id !== id));
  };

  // Calculate total price
  const totalPrice = cartItems.reduce(
    (sum, item) => sum + item.price * item.quantity,
    0
  );

  return (
    <div className="cart-container">
      <h2 className="cart-title">ðŸ›’ Your Shopping Cart</h2>

      <div className="cart-content">
        {/* Left side - cart items */}
        <div className="cart-items">
          {cartItems.length === 0 ? (
            <p>Your cart is empty.</p>
          ) : (
            cartItems.map((item) => (
              <div key={item.id} className="cart-item">
                <img src={item.image} alt={item.name} className="item-image" />
                <div className="item-details">
                  <h4>{item.name}</h4>
                  <p>${item.price} each</p>
                  <div className="quantity-controls">
                    <button onClick={() => decreaseQty(item.id)}>-</button>
                    <span>{item.quantity}</span>
                    <button onClick={() => increaseQty(item.id)}>+</button>
                  </div>
                  <button
                    className="remove-btn"
                    onClick={() => removeItem(item.id)}
                  >
                    Remove
                  </button>
                </div>
              </div>
            ))
          )}
        </div>

        {/* Right side - summary */}
        <div className="cart-summary">
          <h3>Order Summary</h3>
          <p>Items: {cartItems.length}</p>
          <p className="total">Total: ${totalPrice.toFixed(2)}</p>
          <button className="checkout-btn">Proceed to Checkout âœ…</button>
        </div>
      </div>
    </div>
  );
}
